import React, {Component} from 'react';
import Image from './Image';


class Article extends Component {
    state = {
        isOpen: false
    }

    render() {
        const { URL} = this.props;

        const body  = this.state.isOpen ? <section>Bla-bla-bla-bla-bla-bla-bla-bla-bla-bla-bla-bla-bla-bla-bla-bla-bla-bla-bla-bla-bla-bla-bla-bla-bla-bla-bla-bla-bla-bla-bla-bla-bla-bla-bla-bla-bla-bla-bla-bla-bla-bla-bla-bla-bla-bla-bla-bla-bla-bla-bla-bla-bla-bla-bla-bla-bla-bla-bla</section> : '';
        return (
            <div>
                <h2>
                    <Image URL={URL}/>
                    <br />
                    <button onClick={this.handleClick}>
                    {this.state.isOpen ? 'Close' : 'Open'}
                    </button>
                </h2>
                {body}
                <h3>creation date: {(new Date()).toTimeString()}</h3>
            </div>
        )
    }
    handleClick = () => {
        console.log('Button clicked');
        this.setState({
            isOpen: !this.state.isOpen
        }) 
    }

}





export default Article;
